var link=document.querySelector(".button-contact"),popup=document.querySelector(".modal-content"),closelink=document.querySelector(".close-bnt");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-content-show")}),closelink.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-content-show")});var submitBtn=document.querySelector(".button-send"),customerName=popup.querySelector(".customer-name"),customerEmail=popup.querySelector(".customer-email"),customerMessage=popup.querySelector(".customer-text");submitBtn.addEventListener("click",function(e){customerName.value&&customerEmail.value&&customerMessage.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});
